function [ x, y ] = node( x1,y1,x2,y2,l1,l2,n )

x = - (l1^2 - l2^2 - x1^2 + x2^2 - y1^2 + y2^2)/(2*(x1 - x2)) - ((y1 - y2)*(x1*((l1^2 + 2*l1*l2 + l2^2 - x1^2 + 2*x1*x2 - x2^2 - y1^2 + 2*y1*y2 - y2^2)*(- l1^2 + 2*l1*l2 - l2^2 + x1^2 - 2*x1*x2 + x2^2 + y1^2 - 2*y1*y2 + y2^2))^(1/2) - x2*((l1^2 + 2*l1*l2 + l2^2 - x1^2 + 2*x1*x2 - x2^2 - y1^2 + 2*y1*y2 - y2^2)*(- l1^2 + 2*l1*l2 - l2^2 + x1^2 - 2*x1*x2 + x2^2 + y1^2 - 2*y1*y2 + y2^2))^(1/2) - l1^2*y1 + l1^2*y2 + l2^2*y1 - l2^2*y2 + x1^2*y1 + x1^2*y2 + x2^2*y1 + x2^2*y2 - y1*y2^2 - y1^2*y2 + y1^3 + y2^3 - 2*x1*x2*y1 - 2*x1*x2*y2))/(2*(x1 - x2)*(x1^2 - 2*x1*x2 + x2^2 + y1^2 - 2*y1*y2 + y2^2));

if n==1
    y = (x1*((l1^2 + 2*l1*l2 + l2^2 - x1^2 + 2*x1*x2 - x2^2 - y1^2 + 2*y1*y2 - y2^2)*(- l1^2 + 2*l1*l2 - l2^2 + x1^2 - 2*x1*x2 + x2^2 + y1^2 - 2*y1*y2 + y2^2))^(1/2) - x2*((l1^2 + 2*l1*l2 + l2^2 - x1^2 + 2*x1*x2 - x2^2 - y1^2 + 2*y1*y2 - y2^2)*(- l1^2 + 2*l1*l2 - l2^2 + x1^2 - 2*x1*x2 + x2^2 + y1^2 - 2*y1*y2 + y2^2))^(1/2) - l1^2*y1 + l1^2*y2 + l2^2*y1 - l2^2*y2 + x1^2*y1 + x1^2*y2 + x2^2*y1 + x2^2*y2 - y1*y2^2 - y1^2*y2 + y1^3 + y2^3 - 2*x1*x2*y1 - 2*x1*x2*y2)/(2*(x1^2 - 2*x1*x2 + x2^2 + y1^2 - 2*y1*y2 + y2^2));
elseif n==2
    y = (x2*((l1^2 + 2*l1*l2 + l2^2 - x1^2 + 2*x1*x2 - x2^2 - y1^2 + 2*y1*y2 - y2^2)*(- l1^2 + 2*l1*l2 - l2^2 + x1^2 - 2*x1*x2 + x2^2 + y1^2 - 2*y1*y2 + y2^2))^(1/2) - x1*((l1^2 + 2*l1*l2 + l2^2 - x1^2 + 2*x1*x2 - x2^2 - y1^2 + 2*y1*y2 - y2^2)*(- l1^2 + 2*l1*l2 - l2^2 + x1^2 - 2*x1*x2 + x2^2 + y1^2 - 2*y1*y2 + y2^2))^(1/2) - l1^2*y1 + l1^2*y2 + l2^2*y1 - l2^2*y2 + x1^2*y1 + x1^2*y2 + x2^2*y1 + x2^2*y2 - y1*y2^2 - y1^2*y2 + y1^3 + y2^3 - 2*x1*x2*y1 - 2*x1*x2*y2)/(2*(x1^2 - 2*x1*x2 + x2^2 + y1^2 - 2*y1*y2 + y2^2));
end

end

